//                 }
//             },
//             0
//         );
//     }
// }, [hash, pathname]) // do this on route change

  return (
    <div className="appContainer">
      <Header />
      <NavBar />
        <Route exact path="/" component={About} />
        <Route exact path="/">
          <StoneFilter stones={stones} />
        </Route>
        <Route exact path="/" component={Contact} />
    </div>
  )
}

export default App
